import * as Joi from 'joi';
import cpf from './cpf';
import cnpj from './cnpj';
export { cpf, cnpj };
declare const _default: {
    base: Joi.StringSchema;
    name: string;
    language: {
        cnpj: string;
        cpf: string;
    };
    rules: {
        name: string;
        validate(_params: any, value: any, state: any, options: any): any;
    }[];
};
export default _default;
